$(document).ready(function(){$(".b-slider-items").slick({slidesToShow:2,slidesToScroll:2,speed:500,dots:!0,arrows:!1,rows:0,adaptiveHeight:!0,responsive:[{breakpoint:480,settings:{arrows:!1,centerMode:!1,slidesToShow:1,slidesToScroll:1}}],customPaging:function(i,s){return'<span class="dot"></span>'}}).on("setPosition",function(i,s){s.$slides.css("height",s.$slideTrack.height()+"px")}),$(".b-slider-control--right").click(function(){$(this).closest(".b-slider-wrap").find(".b-slider-item").slick("slickNext")}),$(".b-slider-control--left").click(function(){$(this).closest(".b-slider-wrap").find(".b-slider-item").slick("slickPrev")}),AOS.init({offset:300,disable:"mobile",disable:function(){var i=1024;return window.innerWidth<i}})});